import logo from "./logo.svg";
import "./App.css";
import "./style.css";
import { useEffect, useState } from "react";
import supabase from "./supabase";

const CATEGORIES = [
  { name: "technology", color: "#3b82f6" },
  { name: "science", color: "#16a34a" },
  { name: "finance", color: "#ef4444" },
  { name: "society", color: "#eab308" },
  { name: "entertainment", color: "#db2777" },
  { name: "health", color: "#14b8a6" },
  { name: "history", color: "#f97316" },
  { name: "news", color: "#8b5cf6" },
];

const initialFacts = [
  {
    id: 1,
    text: "React is being developed by Meta (formerly facebook)",
    source: "https://opensource.fb.com/",
    category: "technology",
    votesInteresting: 24,
    votesMindblowing: 9,
    votesFalse: 4,
    createdIn: 2021,
  },
  {
    id: 2,
    text: "Millennial dads spend 3 times as much time with their kids than their fathers spent with them. In 1982, 43% of fathers had never changed a diaper. Today, that number is down to 3%",
    source:
      "https://www.mother.ly/parenting/millennial-dads-spend-more-time-with-their-kids",
    category: "society",
    votesInteresting: 11,
    votesMindblowing: 2,
    votesFalse: 0,
    createdIn: 2019,
  },
  {
    id: 3,
    text: "Lisbon is the capital of Portugal",
    source: "https://en.wikipedia.org/wiki/Lisbon",
    category: "society",
    votesInteresting: 8,
    votesMindblowing: 3,
    votesFalse: 1,
    createdIn: 2015,
  },
];

function Counter() {
  const [x, setx] = useState(0);
  return (
    <div>
      <span style={{ fontSize: "40px" }}>{x}</span>
      <button className="btn btn-large" onClick={() => setx(x + 1)}>
        +1
      </button>
    </div>
  );
}

function App() {
  const [showForm, setShowForm] = useState(false);
  const [facts, setFacts] = useState([]);
  const [isLoading, setLoading] = useState(false);

  useEffect(function() {

    async function getFacts(){
    setIsLoading(true);
    const { data: Facts, error } = await supabase
    .from('Facts')
    .select('*');
    setFacts(facts);
    }

    getFacts();
  }, []);

  return (
    <>
      <Header showForm={showForm} setShowForm={setShowForm} />

      {showForm ? <NewfactForm setFacts={setFacts} setShowForm ={setShowForm} /> : null}
      {/* <Counter/> */}

      <main className="main">
      <CategoryFilter />
        {isLoading ? <Loader/> : <CategoryFilter/>}

        <FactList facts={facts} />
      </main>
    </>
  );
}

function Loader(){
  return <p>Loading...</p>
}

function Header({ showForm, setShowForm }) {
  return (
    <header className="header">
      <div className="logo">
        <img src="logo.png" alt="Message Logo" height="70" width="70" />
        <h1>Today I Learned</h1>
      </div>

      <button
        className="btn btn-large share-button"
        onClick={() => setShowForm((show) => !show)}
      >
        {showForm ? "Close" : "Share a fact"}
      </button>
    </header>
  );
}

function isValidHttpUrl(string) {
  let url;

  try {
    url = new URL(string);
  } catch (_) {
    return false;
  }

  return url.protocol === "http:" || url.protocol === "https:";
}

function NewfactForm({setFacts, setShowForm}) {
  const [text, setText] = useState("");
  const [source, setSource] = useState("http://example.com");
  const [category, setCategory] = useState("");
  const textLength = text.length;

  function handleSubmit(e) {
    // Prevent the browser reload
    e.preventDefault();
    console.log(text, source, category);
    // Check if the data is valid. If so, create a new fact.
    if (text && isValidHttpUrl(source) && category && textLength <= 200) {
      // Create a new object
      const newFact = {
        id: Math.round(Math.random() * 100000000),
        text,
        source,
        category,
        votesInteresting: 0,
        votesMindblowing: 0,
        votesFalse: 0,
        createdIn: new Date().getFullYear(),
      };
      // Add the new fact to the UI: add the fact to state
      setFacts((facts) => [newFact, ...facts]);
      // Reset input fields
      setText("");
      setCategory("");
      setSource("");
      // Close the form.
      setShowForm(false)
    }
  }

  return (
    <form className="fact-form" onSubmit={handleSubmit}>
      {" "}
      <input
        type="text"
        name=""
        id=""
        placeholder="Share a fact with the world..."
        value={text}
        onChange={(e) => setText(e.target.value)}
      />
      <span>{200 - textLength}</span>
      <input
        type="text"
        value={source}
        name=""
        id=""
        placeholder="Trustworthy source..."
        onChange={(e) => setSource(e.target.value)}
      />
      <select
        name=""
        id=""
        value={category}
        onChange={(e) => setCategory(e.target.value)}
      >
        <option value="">choose category:</option>
        {CATEGORIES.map((ele) => (
          <option value={ele.name}>{ele.name.toUpperCase()}</option>
        ))}
      </select>
      <button className="btn btn-large">POST</button>
    </form>
  );
}

function CategoryFilter() {
  return (
    <aside>
      <ul>
        <li className="category">
          <button className="btn btn-all-categories">All</button>
        </li>

        {CATEGORIES.map((ele) => (
          <li className="category">
            <button
              className="btn btn-generic"
              style={{ backgroundColor: ele.color }}
            >
              {ele.name}
            </button>
          </li>
        ))}
      </ul>
    </aside>
  );
}

function FactList( {facts} ) {

  return (
    <section>
      <ul className="facts-list">
        {facts.map((fact) => (
          <Fact fact={fact} />
        ))}
      </ul>
    </section>
  );
}

function Fact({ fact }) {
  return (
    <li className="fact">
      <p>
        {fact.text}
        <a className="source" href={fact.source} target="_blank">
          (Source)
        </a>
      </p>
      <span
        className="tag"
        style={{
          backgroundColor: CATEGORIES.find((ele) => ele.name === fact.category)
            .color,
        }}
      >
        {fact.category}
      </span>
      <div className="vote-buttons">
        <button>👍 {fact.votesInteresting}</button>
        <button>🤯 {fact.votesMindblowing}</button>
        <button>⛔️ {fact.votesFalse}</button>
      </div>
    </li>
  );
}

export default App;
