import logo from "./logo.svg";
import "./App.css";
import "./style.css";
import { useState } from "react";

const CATEGORIES = [
  { name: "technology", color: "#3b82f6" },
  { name: "science", color: "#16a34a" },
  { name: "finance", color: "#ef4444" },
  { name: "society", color: "#eab308" },
  { name: "entertainment", color: "#db2777" },
  { name: "health", color: "#14b8a6" },
  { name: "history", color: "#f97316" },
  { name: "news", color: "#8b5cf6" },
];

const initialFacts = [
  {
    id: 1,
    text: "React is being developed by Meta (formerly facebook)",
    source: "https://opensource.fb.com/",
    category: "technology",
    votesInteresting: 24,
    votesMindblowing: 9,
    votesFalse: 4,
    createdIn: 2021,
  },
  {
    id: 2,
    text: "Millennial dads spend 3 times as much time with their kids than their fathers spent with them. In 1982, 43% of fathers had never changed a diaper. Today, that number is down to 3%",
    source:
      "https://www.mother.ly/parenting/millennial-dads-spend-more-time-with-their-kids",
    category: "society",
    votesInteresting: 11,
    votesMindblowing: 2,
    votesFalse: 0,
    createdIn: 2019,
  },
  {
    id: 3,
    text: "Lisbon is the capital of Portugal",
    source: "https://en.wikipedia.org/wiki/Lisbon",
    category: "society",
    votesInteresting: 8,
    votesMindblowing: 3,
    votesFalse: 1,
    createdIn: 2015,
  },
];

function Counter(){
  const [x, setx] = useState(0)
  return <div>
    <span style={{fontSize:"40px"}}>
      {x}
    </span>
    <button className="btn btn-large" onClick={() => setx(x+1)}>+1</button>
  </div>
}

function App() {

  const [showForm, setShowForm] = useState(false);

  return (
    <>
      <Header showFrom ={showForm} setShowForm={setShowForm}/>

      {showForm ? <NewfactForm /> : null}
      {/* <Counter/> */}

      <main className="main">
        <CategoryFilter />
        <FactList />
      </main>
    </>
  );
}

function Header({showForm, setShowForm}){
  return <header className="header">
  <div className="logo">
    <img src="logo.png" alt="Message Logo" height="70" width="70" />
    <h1>Today I Learned</h1>
  </div>

  <button className="btn btn-large share-button" onClick={() => setShowForm((show) => !show)}>{}</button>
</header>
}

function NewfactForm() {
  return <form className="fact-form">Fact form</form>;
}

function CategoryFilter() {
  return (
    <aside>
      <ul>

      <li className="category">
              <button className="btn btn-all-categories">All</button>
            </li>

        {CATEGORIES.map((ele) => (
          <li className="category">
            <button
              className="btn btn-generic"
              style={{ backgroundColor: ele.color }}
            >
              {ele.name}
            </button>
          </li>
        ))}
      </ul>
    </aside>
  );
}

function FactList() {
  const facts = initialFacts;
  return (
    <section>
      <ul className="facts-list">
        {facts.map((fact) => (
          <Fact fact={fact} />
        ))}
      </ul>
    </section>
  );
}

function Fact({ fact }) {
  return (
    <li class="fact">
      <p>
        {fact.text}
        <a className="source" href={fact.source} target="_blank">
          (Source)
        </a>
      </p>
      <span
        className="tag"
        style={{
          backgroundColor: CATEGORIES.find((ele) => ele.name === fact.category)
            .color,
        }}
      >
        {fact.category}
      </span>
      <div className="vote-buttons">
        <button>👍 {fact.votesInteresting}</button>
        <button>🤯 {fact.votesMindblowing}</button>
        <button>⛔️ {fact.votesFalse}</button>
      </div>
    </li>
  );
}

export default App;
